// ============================================================================
// EMBROIDERY PATTERNS
// ============================================================================

export const EMBROIDERY_PATTERNS = {
  maggam: {
    name: 'Maggam Work',
    note: 'Heavy embellishment',
    density: 'heavy',
    threadColors: ['#d4af37', '#ec4899', '#10b981'],
    createPattern: (color = '#ec4899') => {
      const canvas = document.createElement('canvas');
      canvas.width = 70;
      canvas.height = 70;
      const ctx = canvas.getContext('2d');

      ctx.fillStyle = color;
      ctx.beginPath();
      ctx.arc(35, 35, 10, 0, Math.PI * 2);
      ctx.fill();

      for (let i = 0; i < 8; i++) {
        const angle = (i * Math.PI * 2) / 8;
        const x = 35 + Math.cos(angle) * 20;
        const y = 35 + Math.sin(angle) * 20;
        ctx.beginPath();
        ctx.arc(x, y, 4, 0, Math.PI * 2);
        ctx.fill();

        ctx.strokeStyle = color;
        ctx.lineWidth = 1.5;
        ctx.beginPath();
        ctx.moveTo(35, 35);
        ctx.lineTo(x, y);
        ctx.stroke();
      }

      return canvas;
    }
  },
  
  threadWork: {
    name: 'Thread Work',
    note: 'Delicate embroidery',
    density: 'light',
    threadColors: ['#8b5cf6', '#ec4899', '#f59e0b'],
    createPattern: (color = '#8b5cf6') => {
      const canvas = document.createElement('canvas');
      canvas.width = 60;
      canvas.height = 60;
      const ctx = canvas.getContext('2d');

      ctx.strokeStyle = color;
      ctx.lineWidth = 1.5;
      ctx.lineCap = 'round';

      for (let i = 0; i < 4; i++) {
        ctx.beginPath();
        ctx.arc(30, 30, 10 + i * 5, 0, Math.PI * 2);
        ctx.stroke();
      }

      for (let i = 0; i < 12; i++) {
        const angle = (i * Math.PI * 2) / 12;
        ctx.beginPath();
        ctx.moveTo(30, 30);
        ctx.lineTo(30 + Math.cos(angle) * 25, 30 + Math.sin(angle) * 25);
        ctx.stroke();
      }

      return canvas;
    }
  },

  sequins: {
    name: 'Sequin Work',
    note: 'Shimmery sequins',
    density: 'medium',
    threadColors: ['#FFD700', '#C0C0C0', '#CD7F32'],
    createPattern: (color = '#FFD700') => {
      const canvas = document.createElement('canvas');
      canvas.width = 50;
      canvas.height = 50;
      const ctx = canvas.getContext('2d');

      for (let i = 0; i < 9; i++) {
        const x = (i % 3) * 16 + 8;
        const y = Math.floor(i / 3) * 16 + 8;
        
        ctx.fillStyle = color;
        ctx.beginPath();
        ctx.arc(x, y, 4, 0, Math.PI * 2);
        ctx.fill();
        
        ctx.strokeStyle = 'rgba(255,255,255,0.6)';
        ctx.lineWidth = 1;
        ctx.stroke();
      }

      return canvas;
    }
  },

  beadwork: {
    name: 'Bead Work',
    note: 'Intricate beading',
    density: 'heavy',
    threadColors: ['#E6B800', '#DC143C', '#4169E1'],
    createPattern: (color = '#E6B800') => {
      const canvas = document.createElement('canvas');
      canvas.width = 60;
      canvas.height = 60;
      const ctx = canvas.getContext('2d');

      for (let row = 0; row < 4; row++) {
        for (let col = 0; col < 4; col++) {
          const x = col * 15 + 7.5;
          const y = row * 15 + 7.5;
          
          ctx.fillStyle = color;
          ctx.beginPath();
          ctx.arc(x, y, 5, 0, Math.PI * 2);
          ctx.fill();
          
          ctx.fillStyle = 'rgba(255,255,255,0.5)';
          ctx.beginPath();
          ctx.arc(x - 1, y - 1, 2, 0, Math.PI * 2);
          ctx.fill();
        }
      }

      return canvas;
    }
  }
};

// ============================================================================
// FABRIC PRINTS
// ============================================================================

export const FABRIC_PRINTS = {
  floral: {
    name: 'Floral Pattern',
    description: 'Beautiful floral design',
    createPattern: (color = '#FF1493') => {
      const canvas = document.createElement('canvas');
      canvas.width = 100;
      canvas.height = 100;
      const ctx = canvas.getContext('2d');

      ctx.fillStyle = color;
      for (let i = 0; i < 5; i++) {
        const angle = (i * Math.PI * 2) / 5;
        const x = 50 + Math.cos(angle) * 20;
        const y = 50 + Math.sin(angle) * 20;
        ctx.beginPath();
        ctx.arc(x, y, 8, 0, Math.PI * 2);
        ctx.fill();
      }
      
      ctx.fillStyle = '#FFD700';
      ctx.beginPath();
      ctx.arc(50, 50, 6, 0, Math.PI * 2);
      ctx.fill();

      return canvas;
    }
  },

  geometric: {
    name: 'Geometric Pattern',
    description: 'Modern geometric design',
    createPattern: (color = '#4169E1') => {
      const canvas = document.createElement('canvas');
      canvas.width = 80;
      canvas.height = 80;
      const ctx = canvas.getContext('2d');

      ctx.strokeStyle = color;
      ctx.lineWidth = 2;
      
      ctx.beginPath();
      ctx.moveTo(40, 10);
      ctx.lineTo(70, 40);
      ctx.lineTo(40, 70);
      ctx.lineTo(10, 40);
      ctx.closePath();
      ctx.stroke();

      ctx.beginPath();
      ctx.moveTo(40, 20);
      ctx.lineTo(60, 40);
      ctx.lineTo(40, 60);
      ctx.lineTo(20, 40);
      ctx.closePath();
      ctx.stroke();

      return canvas;
    }
  },

  paisley: {
    name: 'Paisley Print',
    description: 'Traditional paisley motif',
    createPattern: (color = '#8B4513') => {
      const canvas = document.createElement('canvas');
      canvas.width = 80;
      canvas.height = 80;
      const ctx = canvas.getContext('2d');

      ctx.fillStyle = color;
      ctx.beginPath();
      ctx.arc(40, 40, 15, 0, Math.PI * 1.5);
      ctx.quadraticCurveTo(55, 25, 55, 40);
      ctx.quadraticCurveTo(55, 55, 40, 55);
      ctx.fill();

      ctx.strokeStyle = 'rgba(0,0,0,0.3)';
      ctx.lineWidth = 1;
      ctx.stroke();

      return canvas;
    }
  },

  dots: {
    name: 'Polka Dots',
    description: 'Classic polka dot pattern',
    createPattern: (color = '#FF6B6B') => {
      const canvas = document.createElement('canvas');
      canvas.width = 60;
      canvas.height = 60;
      const ctx = canvas.getContext('2d');

      ctx.fillStyle = color;
      for (let i = 0; i < 3; i++) {
        for (let j = 0; j < 3; j++) {
          ctx.beginPath();
          ctx.arc(i * 20 + 10, j * 20 + 10, 6, 0, Math.PI * 2);
          ctx.fill();
        }
      }

      return canvas;
    }
  },

  stripes: {
    name: 'Striped Pattern',
    description: 'Elegant stripes',
    createPattern: (color = '#4ECDC4') => {
      const canvas = document.createElement('canvas');
      canvas.width = 60;
      canvas.height = 60;
      const ctx = canvas.getContext('2d');

      ctx.fillStyle = color;
      for (let i = 0; i < 6; i++) {
        if (i % 2 === 0) {
          ctx.fillRect(i * 10, 0, 10, 60);
        }
      }

      return canvas;
    }
  }
};

// ============================================================================
// HELPER FUNCTIONS
// ============================================================================

// Get templates by gender
export const getTemplatesByGender = (gender) => {
  return Object.entries(SVG_TEMPLATES)
    .filter(([_, template]) => template.gender === gender.toLowerCase())
    .reduce((acc, [key, value]) => ({ ...acc, [key]: value }), {});
};

// Get all template names
export const getAllTemplateNames = () => {
  return Object.keys(SVG_TEMPLATES);
};

// Get template by name
export const getTemplateByName = (name) => {
  return SVG_TEMPLATES[name] || SVG_TEMPLATES.Kurti;
};

// Get women's templates
export const getWomensTemplates = () => getTemplatesByGender('women');

// Get men's templates  
export const getMensTemplates = () => getTemplatesByGender('men');